import React, { useState } from "react";
import "../Component/css/QuizSetup.css"; // Import CSS file for styling
import { useInitialContext } from "../context/InitialContext";

function QuizSetup() {
  const [name, setName] = useState("");
  const [deptt, setDeptt] = useState("");
  const [subject, setSubject] = useState("");
  const [topic, setTopic] = useState("");
  const [difficulty, setDifficulty] = useState("");

  const handleStartQuiz = () => {
    console.log("Starting quiz...");
    console.log("Name:", name);
    console.log("Deptt:", deptt);
    console.log("Subject:", subject);
    console.log("Topic:", topic);
    console.log("Difficulty:", difficulty);
    // Add logic to start the quiz
  };

  const { departmentData, subjectData } = useInitialContext();

  const handleDepttChange = (e) => {
    setDeptt(e.target.value);
  };

  const handleSubjectChange = (e) => {
    setSubject(e.target.value);
  };

  const handleTopicChange = (e) => {
    setTopic(e.target.value);
  };

  return (
    <div className="quiz-setup-container">
      <h1>Quiz Setup</h1>
      <div className="input-container">
        <label>Name:</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Enter your name"
        />
      </div>
      <div className="input-container">
        <label>Department:</label>
        <select
          className="form-select"
          name="deptt"
          value={deptt}
          onChange={handleDepttChange}
        >
          <option>Please select Department</option>
          {subjectData &&
            subjectData.map((depttObject, index) => (
              <option key={index} value={depttObject.deptt}>
                {depttObject.deptt}
              </option>
            ))}
        </select>
      </div>
      <div className="input-container">
        <label>Subject:</label>
        <select
          className="form-select"
          name="subject"
          value={subject}
          onChange={handleSubjectChange}
        >
          <option>Please select Subject</option>
          {subjectData &&
            subjectData
              .find((depttObject) => depttObject.deptt === deptt)
              .subjects.map((subjectObject, index) => (
                <option key={index} value={subjectObject.sub}>
                  {subjectObject.sub}
                  {/* {console.log("subject Data" + subjectObject.sub)} */}
                </option>
              ))}
        </select>
      </div>
      <div className="input-container">
        <label>Topic:</label>
        <select
          className="form-select"
          name="topic"
          value={topic}
          onChange={handleTopicChange}
        >
          <option>Please select Topic</option>
          {subjectData &&
            subjectData
              .find((depttObject) => depttObject.deptt === deptt)
              ?.subjects.find((subjectObject) => subjectObject.sub === subject)
              ?.topic?.map((topicItem, index) => (
                <option key={index} value={topicItem}>
                  {topicItem}
                </option>
              ))}
        </select>
      </div>
      <div className="input-container">
        <label>Difficulty:</label>
        <select
          value={difficulty}
          onChange={(e) => setDifficulty(e.target.value)}
          className="select-dropdown"
        >
          <option value="">Select difficulty</option>
          <option value="easy">Easy</option>
          <option value="medium">Medium</option>
          <option value="hard">Hard</option>
        </select>
      </div>
      <div className="start-button-container">
        <button onClick={handleStartQuiz} className="start-button">
          Start Quiz
        </button>
      </div>
    </div>
  );
}

export default QuizSetup;
