import React, { useState } from "react";
import "../Component/css/QuizSetup.css"; // Import CSS file for styling
import { useInitialContext } from "../context/InitialContext";

function QuizSetup() {
  const [name, setName] = useState("");
  const [subject, setSubject] = useState("");
  const [difficulty, setDifficulty] = useState("");

  const handleStartQuiz = () => {
    console.log("Starting quiz...");
    console.log("Name:", name);
    console.log("Subject:", subject);
    console.log("Difficulty:", difficulty);
    // Add logic to start the quiz
  };

  const { subjectInfo } = useInitialContext();

  return (
    <div className="quiz-setup-container">
      <h1>Quiz Setup</h1>
      <div className="input-container">
        <label>Name:</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Enter your name"
        />
      </div>
      <div className="input-container">
        <label>Subject:</label>
        <input
          type="text"
          value={subject}
          onChange={(e) => setSubject(e.target.value)}
          placeholder="Enter the subject"
        />
      </div>
      <div className="input-container">
        <label>Difficulty:</label>
        <select
          value={difficulty}
          onChange={(e) => setDifficulty(e.target.value)}
          className="select-dropdown"
        >
          <option value="">Select difficulty</option>
          <option value="easy">Easy</option>
          <option value="medium">Medium</option>
          <option value="hard">Hard</option>
        </select>
      </div>
      <div className="start-button-container">
        <button onClick={handleStartQuiz} className="start-button">
          Start Quiz
        </button>
      </div>
    </div>
  );
}

export default QuizSetup;
