import React, { useState, useEffect } from "react";
import axios from "axios";

const QbankPage = () => {
  const [qbankData, setQbankData] = useState([]);
  const [subjects, setSubjects] = useState([]);
  const [topcodes, setTopcodes] = useState([]);
  const [formData, setFormData] = useState({
    subcode: "",
    topcode: "",
    validity: "",
    difficulty: "",
    question: "",
    option1: "",
    option2: "",
    option3: "",
    option4: "",
    answer: "",
    reference: "",
  });

  const fetchQbankData = async () => {
    try {
      const response = await axios.get("http://your-php-api-url/view.php");
      setQbankData(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchSubjects = async () => {
    try {
      const response = await axios.get("http://your-php-api-url/subjects.php");
      setSubjects(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchTopcodes = async () => {
    try {
      const response = await axios.get("http://your-php-api-url/topcodes.php");
      setTopcodes(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchQbankData();
    fetchSubjects();
    fetchTopcodes();
  }, []);

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleInsert = async () => {
    try {
      await axios.post("http://your-php-api-url/insert.php", formData);
      fetchQbankData(); // Refresh data after insertion
    } catch (error) {
      console.error(error);
    }
  };

  const handleUpdate = async () => {
    try {
      await axios.post("http://your-php-api-url/update.php", formData);
      fetchQbankData(); // Refresh data after update
    } catch (error) {
      console.error(error);
    }
  };

  const handleDelete = async (qcode) => {
    try {
      await axios.post(`http://your-php-api-url/delete.php?id=${qcode}`);
      fetchQbankData(); // Refresh data after deletion
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h2>Qbank Page</h2>
      {/* Insert form */}
      <div>
        <h3>Insert Record</h3>
        <select
          name="subcode"
          value={formData.subcode}
          onChange={handleInputChange}
        >
          <option value="">Select Subject</option>
          {subjects.map((subject) => (
            <option key={subject.id} value={subject.id}>
              {subject.name}
            </option>
          ))}
        </select>
        <select
          name="topcode"
          value={formData.topcode}
          onChange={handleInputChange}
        >
          <option value="">Select Topcode</option>
          {topcodes.map((topcode) => (
            <option key={topcode.id} value={topcode.id}>
              {topcode.name}
            </option>
          ))}
        </select>
        <input
          type="text"
          name="validity"
          value={formData.validity}
          onChange={handleInputChange}
          placeholder="Validity"
        />
        <input
          type="text"
          name="difficulty"
          value={formData.difficulty}
          onChange={handleInputChange}
          placeholder="Difficulty"
        />
        {/* Add more input fields for other attributes */}
        <button onClick={handleInsert}>Insert</button>
      </div>

      {/* Update form */}
      <div>
        <h3>Update Record</h3>
        <input
          type="text"
          name="qcode"
          value={formData.qcode}
          onChange={handleInputChange}
          placeholder="Qcode to update"
        />
        {/* Add dropdowns for subject and topcode */}
        <select
          name="subcode"
          value={formData.subcode}
          onChange={handleInputChange}
        >
          <option value="">Select Subject</option>
          {subjects.map((subject) => (
            <option key={subject.id} value={subject.id}>
              {subject.name}
            </option>
          ))}
        </select>
        <select
          name="topcode"
          value={formData.topcode}
          onChange={handleInputChange}
        >
          <option value="">Select Topcode</option>
          {topcodes.map((topcode) => (
            <option key={topcode.id} value={topcode.id}>
              {topcode.name}
            </option>
          ))}
        </select>
        <input
          type="text"
          name="validity"
          value={formData.validity}
          onChange={handleInputChange}
          placeholder="Validity"
        />
        <input
          type="text"
          name="difficulty"
          value={formData.difficulty}
          onChange={handleInputChange}
          placeholder="Difficulty"
        />
        {/* Add more input fields for other attributes */}
        <button onClick={handleUpdate}>Update</button>
      </div>

      {/* Delete form */}
      <div>
        <h3>Delete Record</h3>
        <input
          type="text"
          name="qcode"
          value={formData.qcode}
          onChange={handleInputChange}
          placeholder="Qcode to delete"
        />
        <button onClick={() => handleDelete(formData.qcode)}>Delete</button>
      </div>

      {/* View Qbank table */}
      <div>
        <h3>View Qbank Table</h3>
        <ul>
          {qbankData.map((item) => (
            <li key={item.qcode}>
              {/* Display each Qbank record */}
              <span>{item.qcode}</span>
              {/* Display more attributes */}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default QbankPage;
