import React, { useState, useEffect } from "react";
import axios from "axios";

const EditableQbankData = () => {
  const [qbankData, setQbankData] = useState([]);
  const [subjects, setSubjects] = useState([]);
  const [topcodes, setTopcodes] = useState([]);
  const [formData, setFormData] = useState({
    qcode: "",
    subcode: "",
    topcode: "",
    question: "",
    option1: "",
    option2: "",
    option3: "",
    option4: "",
    answer: "",
    reference: "",
  });

  useEffect(() => {
    fetchSubjects();
  }, []);

  const fetchSubjects = async () => {
    try {
      const response = await axios.get(
        "https://railwaymcq.com/student/subMaster_api.php"
      );
      setSubjects(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchTopcodes = async (subcode) => {
    try {
      const response = await axios.get(
        `https://railwaymcq.com/student/topicMaster_api.php?subcode=${subcode}`
      );
      setTopcodes(response.data);
    } catch (error) {
      console.error(error);
      setTopcodes([]);
    }
  };

  const fetchData = async () => {
    try {
      const response = await axios.get(
        `https://railwaymcq.com/student/Qbank_op_api.php?subcode=${formData.subcode}&topcode=${formData.topcode}`
      );
      setQbankData(response.data);
    } catch (error) {
      console.error(error);
      setQbankData([]);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFetchData = () => {
    fetchData();
  };

  const handleUpdate = async (index) => {
    try {
      const updatedItem = qbankData[index];
      console.log("update item" + updatedItem);
      const response = await axios.put(
        `https://railwaymcq.com/student/update_qbank.php`,
        updatedItem
      );
      console.log("Update successful:", response.data);
      // Optionally, you can update the local state with the updated data
      setQbankData((prevData) => {
        const newData = [...prevData];
        newData[index] = response.data;
        return newData;
      });
    } catch (error) {
      console.error("Error updating data:", error);
    }
  };

  return (
    <div>
      <h2>Editable Qbank Data</h2>
      <div>
        <select
          name="subcode"
          onChange={(e) => {
            handleInputChange(e);
            fetchTopcodes(e.target.value);
          }}
        >
          <option key="default" value="">
            Select Subject
          </option>
          {subjects.map((subject, index) => (
            <option key={index} value={subject.subcode}>
              {subject.sub}+{subject.subcode}
            </option>
          ))}
        </select>
        <select name="topcode" onChange={handleInputChange}>
          <option value="">Select Topic</option>
          {topcodes.map((topcode) => (
            <option key={topcode.topcode} value={topcode.topcode}>
              {topcode.topic}
            </option>
          ))}
        </select>
        <button onClick={handleFetchData}>Fetch Data</button>
      </div>
      <table>
        <thead>
          <tr>
            <th>Question</th>
            <th>Option 1</th>
            <th>Option 2</th>
            <th>Option 3</th>
            <th>Option 4</th>
            <th>Answer</th>
            <th>Reference</th>
            <th>Update</th>
          </tr>
        </thead>
        <tbody>
          {qbankData.map((item, index) => (
            <tr key={index}>
              <td>
                <textarea
                  rows="6"
                  cols="50"
                  name="question"
                  value={item.question}
                  onChange={(e) => handleInputChange(e)}
                />
              </td>
              <td>
                <input
                  type="text"
                  name="option1"
                  value={item.option1}
                  onChange={(e) => handleInputChange(e)}
                />
              </td>
              <td>
                <input
                  type="text"
                  name="option2"
                  value={item.option2}
                  onChange={(e) => handleInputChange(e)}
                />
              </td>
              <td>
                <input
                  type="text"
                  name="option3"
                  value={item.option3}
                  onChange={(e) => handleInputChange(e)}
                />
              </td>
              <td>
                <input
                  type="text"
                  name="option4"
                  value={item.option4}
                  onChange={(e) => handleInputChange(e)}
                />
              </td>
              <td>
                <input
                  type="text"
                  name="answer"
                  value={item.answer}
                  onChange={(e) => handleInputChange(e)}
                />
              </td>
              <td>
                <input
                  type="text"
                  name="reference"
                  value={item.reference}
                  onChange={(e) => handleInputChange(e)}
                />
              </td>
              <td>
                <button onClick={() => handleUpdate(index)}>Update</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default EditableQbankData;
