import React, { useState, useEffect } from "react";
import axios from "axios";

const EditableQbankData = () => {
  const [qbankData, setQbankData] = useState([]);
  const [subjects, setSubjects] = useState([]);
  const [topcodes, setTopcodes] = useState([]);
  const [formData, setFormData] = useState({
    subcode: "",
    topcode: "",
    question: "",
    option1: "",
    option2: "",
    option3: "",
    option4: "",
    answer: "",
    reference: "",
  });

  useEffect(() => {
    fetchSubjects();
  }, []);

  const fetchSubjects = async () => {
    try {
      const response = await axios.get(
        "https://railwaymcq.com/student/subMaster_api.php"
      );
      setSubjects(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchTopcodes = async (subcode) => {
    try {
      const response = await axios.get(
        `https://railwaymcq.com/student/topicMaster_api.php?subcode=${subcode}`
      );
      setTopcodes(response.data);
    } catch (error) {
      console.error(error);
      setTopcodes([]);
    }
  };

  const fetchData = async () => {
    try {
      const response = await axios.get(
        `https://railwaymcq.com/student/Qbank_op_api.php?subcode=${formData.subcode}&topcode=${formData.topcode}`
      );
      setQbankData(response.data);
    } catch (error) {
      console.error(error);
      setQbankData([]);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFetchData = () => {
    fetchData();
  };

  const handleUpdate = (index, columnName) => {
    // Implement update functionality for the cell at the given index and column name
  };

  return (
    <div>
      <h2>Editable Qbank Data</h2>
      <div>
        <select
          name="subcode"
          onChange={(e) => {
            handleInputChange(e);
            fetchTopcodes(e.target.value);
          }}
        >
          <option key="default" value="">
            Select Subject
          </option>
          {subjects.map((subject, index) => (
            <option key={index} value={subject.subcode}>
              {subject.sub}+{subject.subcode}
            </option>
          ))}
        </select>
        <select name="topcode" onChange={handleInputChange}>
          <option value="">Select Topic</option>
          {topcodes.map((topcode) => (
            <option key={topcode.topcode} value={topcode.topcode}>
              {topcode.topic}
            </option>
          ))}
        </select>
        <button onClick={handleFetchData}>Fetch Data</button>
      </div>
      <table>
        <thead>
          <tr>
            <th>Question</th>
            <th>Option 1</th>
            <th>Option 2</th>
            <th>Option 3</th>
            <th>Option 4</th>
            <th>Answer</th>
            <th>Reference</th>
          </tr>
        </thead>
        <tbody>
          {qbankData.map((item, rowIndex) => (
            <tr key={rowIndex}>
              <td>
                <textarea
                  rows="6"
                  cols="50"
                  name="question"
                  value={item.question}
                  onChange={(e) => handleInputChange(e)}
                />
                <button onClick={() => handleUpdate(rowIndex, "question")}>
                  Update
                </button>
              </td>
              <td>
                <input
                  type="text"
                  name="option1"
                  value={item.option1}
                  onChange={(e) => handleInputChange(e)}
                />
                <button onClick={() => handleUpdate(rowIndex, "option1")}>
                  Update
                </button>
              </td>
              <td>
                <input
                  type="text"
                  name="option2"
                  value={item.option2}
                  onChange={(e) => handleInputChange(e)}
                />
                <button onClick={() => handleUpdate(rowIndex, "option2")}>
                  Update
                </button>
              </td>
              <td>
                <input
                  type="text"
                  name="option3"
                  value={item.option3}
                  onChange={(e) => handleInputChange(e)}
                />
                <button onClick={() => handleUpdate(rowIndex, "option3")}>
                  Update
                </button>
              </td>
              <td>
                <input
                  type="text"
                  name="option4"
                  value={item.option4}
                  onChange={(e) => handleInputChange(e)}
                />
                <button onClick={() => handleUpdate(rowIndex, "option4")}>
                  Update
                </button>
              </td>
              <td>
                <input
                  type="text"
                  name="answer"
                  value={item.answer}
                  onChange={(e) => handleInputChange(e)}
                />
                <button onClick={() => handleUpdate(rowIndex, "answer")}>
                  Update
                </button>
              </td>
              <td>
                <input
                  type="text"
                  name="reference"
                  value={item.reference}
                  onChange={(e) => handleInputChange(e)}
                />
                <button onClick={() => handleUpdate(rowIndex, "reference")}>
                  Update
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default EditableQbankData;
