import React, { useState } from "react";

export default function FeedBack_user(props) {
  const handleUpClick = () => {
    console.log("Upper case was clicked");
    let newText = text.toUpperCase();
    setText(newText);
  };

  const handleLoClick = () => {
    console.log("Lower case was clicked");
    let newText = text.toLowerCase();
    setText(newText);
  };

  function handleOnchange(event) {
    console.log("Handle On Change" + text);
    setText(event.target.value);
    const name = event.target.name;
    const value = event.targate.value;
  }
  const [text, setText] = useState("");
  return (
    <div>
      <h6>{props.heading}</h6>
      <div className="mb-3">
        &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;
        &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;
        &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;
        <button type="button" className="btn btn-primary position-relative">
          Total Words
          <span
            className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger"
            align
            right
          >
            {text.split(" ").length - 1}
            <span className="visually-hidden">unread messages</span>
          </span>
        </button>
        &nbsp; &nbsp;
        <button type="button" className="btn btn-primary position-relative">
          Total character
          <span className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger">
            {text.length}{" "}
            <span className="visually-hidden">unread messages</span>
          </span>
        </button>
        <select
          className="form-select"
          aria-label="Default select example"
          onChange={handleOnchange}
        >
          <option selected>Please select Zone</option>
          <option value="1">WCR</option>
          <option value="2">ECR</option>
          <option value="3">NCR</option>
          <option value="3">NCR</option>
        </select>
        <select
          className="form-select"
          aria-label="Default select example"
          onChange={handleOnchange}
        >
          <option selected>Please select Division</option>
          <option value="1">Jabalpur</option>
          <option value="2">Kota</option>
          <option value="3">Bhopal</option>
        </select>
        <label htmlFor="mybox" className="form-label" align="left">
          <strong>Please feel free to leave any Feedback here</strong>
        </label>
        <textarea
          className="form-control"
          value={text}
          onChange={handleOnchange}
          id="myBox"
          rows="4"
          col="8"
        ></textarea>
      </div>
      <button className="btn btn-primary" onClick={handleUpClick}>
        Convert to upper Case
      </button>
      &nbsp; &nbsp;
      <button className="btn btn-primary" onClick={handleLoClick}>
        Convert to Lower Case
      </button>
      &nbsp; &nbsp;
      <button className="btn btn-primary" onClick={handleOnchange}>
        Save Feedback
      </button>
    </div>
  );
}
