import React, { useState, useEffect } from "react";
import axios from "axios";

const EditableQbankData = () => {
  const [qbankData, setQbankData] = useState([]);
  const [subjects, setSubjects] = useState([]);
  const [topcodes, setTopcodes] = useState([]);
  const [formData, setFormData] = useState({
    qcode: "",
    subcode: "",
    topcode: "",
    question: "",
    option1: "",
    option2: "",
    option3: "",
    option4: "",
    answer: "",
    reference: "",
  });
  const [editingIndex, setEditingIndex] = useState(null); // State variable to track the index of the row being edited

  useEffect(() => {
    fetchSubjects();
  }, []);

  const fetchSubjects = async () => {
    try {
      const response = await axios.get(
        "https://railwaymcq.com/student/subMaster_api.php"
      );
      setSubjects(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchTopcodes = async (subcode) => {
    try {
      const response = await axios.get(
        `https://railwaymcq.com/student/topicMaster_api.php?subcode=${subcode}`
      );
      setTopcodes(response.data);
    } catch (error) {
      console.error(error);
      setTopcodes([]);
    }
  };

  const fetchData = async () => {
    try {
      const response = await axios.get(
        `https://railwaymcq.com/student/Qbank_op_api.php?subcode=${formData.subcode}&topcode=${formData.topcode}`
      );
      setQbankData(response.data);
    } catch (error) {
      console.error(error);
      setQbankData([]);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFetchData = () => {
    fetchData();
  };

  const handleUpdate = async (index) => {
    try {
      const updatedItem = qbankData[index];
      const response = await axios.put(
        "https://railwaymcq.com/student/Qbank_op_api.php",
        updatedItem
      );
      setQbankData((prevData) => {
        const newData = [...prevData];
        newData[index] = response.data;
        return newData;
      });
      setEditingIndex(null); // Reset editing index after update
    } catch (error) {
      console.error("Error updating data:", error);
    }
  };

  const handleEdit = (index) => {
    setEditingIndex(index); // Set the editing index to the clicked row index
  };

  return (
    <div>
      <h2>Editable Qbank Data</h2>
      <div>
        <select
          name="subcode"
          value={formData.subcode}
          onChange={(e) => {
            handleInputChange(e);
            fetchTopcodes(e.target.value);
          }}
        >
          <option value="">Select Subject</option>
          {subjects.map((subject, index) => (
            <option key={index} value={subject.subcode}>
              {subject.sub}+{subject.subcode}
            </option>
          ))}
        </select>
        <select
          name="topcode"
          value={formData.topcode}
          onChange={handleInputChange}
        >
          <option value="">Select Topic</option>
          {topcodes.map((topcode) => (
            <option key={topcode.topcode} value={topcode.topcode}>
              {topcode.topic}
            </option>
          ))}
        </select>
        <button onClick={handleFetchData}>Fetch Data</button>
      </div>
      <table>
        <thead>
          <tr>
            <th>Question</th>
            <th>Option 1</th>
            <th>Option 2</th>
            <th>Option 3</th>
            <th>Option 4</th>
            <th>Answer</th>
            <th>Reference</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {qbankData.map((item, index) => (
            <tr key={index}>
              <td>
                {editingIndex === index ? (
                  <textarea
                    rows="6"
                    cols="50"
                    name="question"
                    value={item.question}
                    onChange={(e) => handleInputChange(e)}
                  />
                ) : (
                  item.question
                )}
              </td>
              <td>
                {editingIndex === index ? (
                  <input
                    type="text"
                    name="option1"
                    value={item.option1}
                    onChange={(e) => handleInputChange(e)}
                  />
                ) : (
                  item.option1
                )}
              </td>
              <td>
                {editingIndex === index ? (
                  <input
                    type="text"
                    name="option2"
                    value={item.option2}
                    onChange={(e) => handleInputChange(e)}
                  />
                ) : (
                  item.option2
                )}
              </td>
              <td>
                {editingIndex === index ? (
                  <input
                    type="text"
                    name="option3"
                    value={item.option3}
                    onChange={(e) => handleInputChange(e)}
                  />
                ) : (
                  item.option3
                )}
              </td>
              <td>
                {editingIndex === index ? (
                  <input
                    type="text"
                    name="option4"
                    value={item.option4}
                    onChange={(e) => handleInputChange(e)}
                  />
                ) : (
                  item.option4
                )}
              </td>
              <td>
                {editingIndex === index ? (
                  <input
                    type="text"
                    name="answer"
                    value={item.answer}
                    onChange={(e) => handleInputChange(e)}
                  />
                ) : (
                  item.answer
                )}
              </td>
              <td>
                {editingIndex === index ? (
                  <input
                    type="text"
                    name="reference"
                    value={item.reference}
                    onChange={(e) => handleInputChange(e)}
                  />
                ) : (
                  item.reference
                )}
              </td>
              <td>
                {editingIndex === index ? (
                  <button onClick={() => handleUpdate(index)}>Update</button>
                ) : (
                  <button onClick={() => handleEdit(index)}>Edit</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default EditableQbankData;
