import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import "./feedback.css"; // Import CSS file

// Axios Instance
const ai = axios.create({
  baseURL: "https://railwaymcq.com/student/dbfeedback_api.php",
});

//useContext start hear

const ZoneDivisionProvider = ({ children }) => {
  const [selectedZone, setSelectedZone] = useState("");
  const [selectedDivision, setSelectedDivision] = useState("");
  const [divisionOptions, setDivisionOptions] = useState([]);

  const handleZoneChange = (zone) => {
    setSelectedZone(zone);
    const divisions = data.find((item) => Object.keys(item)[0] === zone)[zone];
    setDivisionOptions(divisions);
  };

  const handleDivisionChange = (division) => {
    setSelectedDivision(division);
  };

  return (
    <ZoneDivisionContext.Provider
      value={{
        selectedZone,
        selectedDivision,
        divisionOptions,
        handleZoneChange,
        handleDivisionChange,
      }}
    >
      {children}
    </ZoneDivisionContext.Provider>
  );
};

// Child component to select zone and division

const [options, setOptions] = useState([]); // for zone option create a state

const ZoneDivisionSelector = () => {
  const {
    selectedZone,
    selectedDivision,
    divisionOptions,
    handleZoneChange,
    handleDivisionChange,
  } = useContext(ZoneDivisionContext);

  return (
    <div>
      <h2>Select Zone:</h2>
      <select
        value={selectedZone}
        onChange={(e) => handleZoneChange(e.target.value)}
      >
        <option value="">Select Zone</option>
        {data.map((item) => (
          <option key={Object.keys(item)[0]} value={Object.keys(item)[0]}>
            {Object.keys(item)[0]}
          </option>
        ))}
      </select>

      <h2>Select Division:</h2>
      <select
        value={selectedDivision}
        onChange={(e) => handleDivisionChange(e.target.value)}
      >
        <option value="">Select Division</option>
        {divisionOptions.map((division) => (
          <option key={division} value={division}>
            {division}
          </option>
        ))}
      </select>
    </div>
  );
};

//useContext end hear

export default function FeedBack_user(props) {
  const [formData, setFormData] = useState({
    zone: "",
    division: "",
    deptt: "",
    message: "",
    message_by: "",
  });

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;

    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));

    // Validate input
    if (name === "name" && value.length < 3) {
      setErrors({ ...errors, name: "Name must be at least 3 characters long" });
    } else if (name === "email" && !/\S+@\S+\.\S+/.test(value)) {
      setErrors({ ...errors, email: "Invalid email address" });
    } else {
      setErrors({ ...errors, [name]: "" });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      console.log("Form Data:", JSON.stringify(formData, null, 2));

      if (Object.values(errors).some((error) => error !== "")) {
        alert("Please fix the errors before submitting the form");
        return;
      }
      const response = await axios.post(
        "https://railwaymcq.com/student/dbfeedback_api.php",
        formData
      );
      // const response = await ai.post("", formData);
      console.log("Data added successfully:", response.data);
      // Clear form fields after successful submission
      setFormData({
        zone: "",
        division: "",
        deptt: "",
        message: "",
        message_by: "",
      });
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  // for fetch data from backend start
  const [getFeedback, setFeedback] = useState([]);

  const getAllFeedback = async () => {
    try {
      const Feedbackdata = await ai.get("/Feedback/list/");
      setFeedback(Feedbackdata.data);
    } catch (error) {
      console.log(error);
    }
  };

  const handleOptionClick = (value) => {
    console.log("Selected option:", value);
    // You can add your logic here for handling the selected option
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          "https://railwaymcq.com/ohe/zone_division_api.php"
        );
        setOptions(response.data);
      } catch (error) {
        console.error("Error fetching options:", error);
      }
    };
    getAllFeedback();
    fetchData();
  }, []);

  return (
    <div className="feedback-container">
      <form onSubmit={handleSubmit}>
        <h6>{props.heading}</h6>

        {/* {options.map((option) => (
          <button
            key={option.id}
            onClick={() => handleOptionClick(option.value)}
          >
            {option.label}
          </button>
        ))} */}

        <select
          className="form-select"
          name="zone"
          value={formData.zone}
          onChange={handleChange}
        >
          <option value="">Please select Zone</option>
          <option value="WCR">WCR</option>
          <option value="ECR">ECR</option>
          <option value="NCR">NCR</option>
        </select>

        <select
          className="form-select"
          name="division"
          value={formData.division}
          onChange={handleChange}
        >
          <option>Please select Division</option>
          <option value="JBP">Jabalpur</option>
          <option value="KOTA">Kota</option>
          <option value="BPL">Bhopal</option>
        </select>

        <select
          className="form-select"
          name="deptt"
          value={formData.deptt}
          onChange={handleChange}
        >
          <option>Please select Department</option>
          <option value="GEN. ELECTRICAL">GEN. ELECTRICAL</option>
          <option value="TRD">TRD</option>
          <option value="TRS">TRS</option>
        </select>
        <label htmlFor="mybox" className="form-label">
          <strong>Please feel free to leave any Feedback here</strong>
        </label>
        <textarea
          type="text"
          name="message"
          value={formData.message}
          onChange={handleChange}
          className="form-control"
          id="myBox"
          rows="4"
          col="8"
        ></textarea>

        <input
          type="text"
          className="custom-textbox" // Apply custom class for styling
          name="message_by"
          value={formData.message_by}
          onChange={handleChange}
          placeholder="Enter your good name please..."
        />

        <button type="submit" className="btn btn-primary">
          Save Feedback
        </button>
        <ul className="feedback-list">
          <h4>Your Feedbacks</h4>
          {getFeedback.map((item) => (
            <li key={item.id}>
              {item.zone} - {item.division} - {item.message} - {item.date}
            </li>
          ))}
        </ul>
      </form>
    </div>
  );
}
