import React, { useState, useEffect } from "react";
import axios from "axios";
import { useLocation } from "react-router-dom";
import "../css/Quiz.css";

function Quiz1() {
  const [quizData, setQuizData] = useState([]);
  const [userAnswers, setUserAnswers] = useState({});
  const [score, setScore] = useState(0);
  const location = useLocation();
  const { state: receivedData } = location;

  useEffect(() => {
    const fetchQuizData = async () => {
      try {
        const response = await axios.get(
          `https://railwaymcq.com/student/mcq_api.php?topicCode=${receivedData?.topicCode}&difficultyLevel=${receivedData?.difficulty}&subjectcode=${receivedData?.subjectcode}`
        );
        setQuizData(response.data);
      } catch (error) {
        console.error("Error fetching quiz data:", error);
      }
    };

    fetchQuizData();
  }, [receivedData]);

  const handleOptionSelect = (questionId, selectedOption) => {
    setUserAnswers({ ...userAnswers, [questionId]: selectedOption });
  };

  const handleSubmit = () => {
    let totalScore = 0;
    quizData.forEach((question) => {
      if (userAnswers[question.qcode] === question.answer.trim()) {
        totalScore++;
      }
    });
    setScore(totalScore);
  };

  const getColorForOption = (option, questionId) => {
    if (userAnswers[questionId] === option) {
      if (
        option ===
        quizData.find((question) => question.qcode === questionId).answer.trim()
      ) {
        return "green";
      } else {
        return "red";
      }
    }
    return "";
  };

  return (
    <div className="quiz-container">
      <h1 className="quiz-heading">Quiz Attended by: {receivedData?.name}</h1>
      {quizData.length ? (
        <>
          {quizData.map((question, index) => (
            <div key={question.qcode} className="question-box">
              <h3 className="question-number">Q{index + 1}:</h3>
              <h3 className="question-text">{question.question}</h3>
              <ul className="options-list">
                {[
                  question.option1,
                  question.option2,
                  question.option3,
                  question.option4,
                ].map((option, idx) => (
                  <li key={idx} className="option-item">
                    <input
                      type="radio"
                      id={`${question.qcode}_option${idx + 1}`}
                      name={question.qcode}
                      value={option}
                      checked={userAnswers[question.qcode] === option}
                      onChange={() =>
                        handleOptionSelect(question.qcode, option)
                      }
                    />
                    <label
                      htmlFor={`${question.qcode}_option${idx + 1}`}
                      className={`option-label ${getColorForOption(
                        option,
                        question.qcode
                      )}`}
                    >
                      {option}
                    </label>
                  </li>
                ))}
              </ul>
              <p className="correct-answer">
                Correct Answer: {question.answer}
              </p>
            </div>
          ))}
          <button onClick={handleSubmit} className="submit-button">
            Submit
          </button>
          <strong className="score-text">Your Score: {score}</strong>
        </>
      ) : (
        <div className="no-quiz-container">
          <h4 className="no-quiz-text">
            Sorry! Currently, there are no quizzes available for this topic.
          </h4>
          <button className="go-back-button" onClick={handleGoBack}>
            Go Back
          </button>
        </div>
      )}
    </div>
  );
}

export default Quiz1;
