import React, { useState, useEffect } from "react";
import axios from "axios";

const EditableQbankData = () => {
  const [qbankData, setQbankData] = useState([]);
  const [subjects, setSubjects] = useState([]);
  const [topcodes, setTopcodes] = useState([]);
  const [formData, setFormData] = useState({
    qcode: "",
    subcode: "",
    topcode: "",
    question: "",
    option1: "",
    option2: "",
    option3: "",
    option4: "",
    answer: "",
    reference: "",
  });
  const [fetchingData, setFetchingData] = useState(false); // State variable to track data fetching status

  useEffect(() => {
    fetchSubjects();
  }, []);

  const fetchSubjects = async () => {
    try {
      setFetchingData(true); // Start fetching data
      const response = await axios.get(
        "https://railwaymcq.com/student/subMaster_api.php"
      );
      setSubjects(response.data);
      setFetchingData(false); // Data fetching complete
    } catch (error) {
      console.error(error);
      setFetchingData(false); // Data fetching complete (even if it fails)
    }
  };

  const fetchTopcodes = async (subcode) => {
    try {
      setFetchingData(true); // Start fetching data
      const response = await axios.get(
        `https://railwaymcq.com/student/topicMaster_api.php?subcode=${subcode}`
      );
      setTopcodes(response.data);
      setFetchingData(false); // Data fetching complete
    } catch (error) {
      console.error(error);
      setTopcodes([]);
      setFetchingData(false); // Data fetching complete (even if it fails)
    }
  };

  const fetchData = async () => {
    try {
      setFetchingData(true); // Start fetching data
      const response = await axios.get(
        `https://railwaymcq.com/student/Qbank_op_api.php?subcode=${formData.subcode}&topcode=${formData.topcode}`
      );
      setQbankData(response.data);
      setFetchingData(false); // Data fetching complete
    } catch (error) {
      console.error(error);
      setQbankData([]);
      setFetchingData(false); // Data fetching complete (even if it fails)
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFetchData = () => {
    fetchData();
  };

  const handleUpdate = async (index) => {
    try {
      const updatedItem = qbankData[index];
      const response = await axios.put(
        "https://railwaymcq.com/student/Qbank_op_api.php",
        updatedItem
      );
      setQbankData((prevData) => {
        const newData = [...prevData];
        newData[index] = response.data;
        return newData;
      });
    } catch (error) {
      console.error("Error updating data:", error);
    }
  };

  return (
    <div>
      <h2>Editable Qbank Data</h2>
      <div>
        <select
          name="subcode"
          value={formData.subcode}
          onChange={(e) => {
            handleInputChange(e);
            fetchTopcodes(e.target.value);
          }}
        >
          <option value="">Select Subject</option>
          {subjects.map((subject, index) => (
            <option key={index} value={subject.subcode}>
              {subject.sub}+{subject.subcode}
            </option>
          ))}
        </select>
        <select
          name="topcode"
          value={formData.topcode}
          onChange={(e) =>
            setFormData({ ...formData, topcode: e.target.value })
          }
        >
          <option value="">Select Topic</option>
          {topcodes.map((topcode, index) => (
            <option key={index} value={topcode.topcode}>
              {topcode.topic}
            </option>
          ))}
        </select>
        <button onClick={handleFetchData}>Fetch Data</button>
        {fetchingData && <p>Loading...</p>}{" "}
        {/* Display loading indicator if data is being fetched */}
      </div>
      <table>
        <thead>
          <tr>
            <th>Question</th>
            <th>Option 1</th>
            <th>Option 2</th>
            <th>Option 3</th>
            <th>Option 4</th>
            <th>Answer</th>
            <th>Reference</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {qbankData.map((item, index) => (
            <tr key={index}>
              <td>{item.question}</td>
              <td>{item.option1}</td>
              <td>{item.option2}</td>
              <td>{item.option3}</td>
              <td>{item.option4}</td>
              <td>{item.answer}</td>
              <td>{item.reference}</td>
              <td>
                <button onClick={() => handleUpdate(index)}>Update</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default EditableQbankData;
