import React, { useState, useEffect } from "react";
import "../Component/css/QuizSetup.css"; // Import CSS file for styling
import { useNavigate } from "react-router-dom"; // Import useNavigate hook
import { useInitialContext } from "../context/InitialContext";

function QuizSetup() {
  const [name, setName] = useState("");
  const [deptt, setDeptt] = useState("");
  const [depttcode, setDepttcode] = useState("");
  const [subject, setSubject] = useState("");
  const [topic, setTopic] = useState({});
  const [selectedSubject, setSelectedSubject] = useState({
    sub: "",
    subCode: "",
  });
  const [selectedTopic, setSelectedTopic] = useState({
    topic: "",
    topicCode: "",
  });

  console.log(selectedTopic);
  const [difficulty, setDifficulty] = useState("");
  const [deptObj, setDeptObj] = useState({});

  const navigate = useNavigate(); // Initialize useNavigate hook

  const handleStartQuiz = () => {
    // Add logic to start the quiz
    // Redirect to another page upon successful button click
    navigate("/quiz"); // Replace "/quiz" with the path to your quiz page
  };

  /* start useEffect for data transfer*/

  useEffect(() => {
    // Set default department and subject if available
    // if (departmentData.length > 0) {
    //   setDeptt(departmentData[0].deptt);
    //   setDeptObj(departmentData[0]);
    // }
    if (subjectData.length > 0) {
      const defaultSubject = subjectData[0].subjects[0];
      setSelectedSubject({
        sub: defaultSubject.sub,
        subCode: defaultSubject.subCode,
      });
    }
  }, [subjectData]);

  /*End useEffect*/

  const { departmentData, subjectData } = useInitialContext();

  const handleDepttChange = (e) => {
    setDeptt(e.target.value);
    setDeptObj(
      subjectData?.find((depttObject) => depttObject.deptt === e.target.value)
    );
  };
  const handleDepttcodeChange = (e) => {
    setDepttcode(e.target.value);
  };

  const handleSubjectChange = (e) => {
    setSubject(e.target.value);
    const subjectAndSubjectCode = e.target.value;
    const sub = subjectAndSubjectCode.replace(/[^a-zA-Z]/g, ""); // Remove non-alphabetic characters
    const subCode = subjectAndSubjectCode.replace(/[a-zA-Z]/g, ""); // Remove alphabetic characters
    setSelectedSubject({ sub: sub, subCode: subCode });
  };

  const handleTopicChange = (e) => {
    setTopic(e.target.value);
    const topicAndCode = e.target.value;
    const parts = topicAndCode.split(",").map((part) => part.trim());

    let topicCode = "";
    let topic = "";

    // Iterate over each part to find topic code and topic
    parts.forEach((part) => {
      if (part.startsWith("topicCode=")) {
        topicCode = part.split("=")[1];
      } else if (part.startsWith("topic=")) {
        topic = part.split("=")[1];
      }
    });

    // console.log("Getting topic :" + topic + "topic cdoe: " + topicCode); // Output: "110"

    setSelectedTopic({ topic: topic, topicCode: topicCode });
  };

  return (
    <div className="quiz-setup-container">
      <h1>Quiz Setup</h1>
      <div className="input-container">
        <label>Name:</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Enter your name"
        />
      </div>
      <div className="input-container">
        <label>Department:</label>
        <select
          className="form-select"
          name="deptt"
          value={deptt}
          onChange={handleDepttChange}
        >
          <option>Please select Department</option>
          {subjectData &&
            subjectData.map((depttObject, index) => (
              <option key={index} value={depttObject.depttcode}>
                {depttObject.deptt}
                {console.log(depttObject.depttcode)}
              </option>
            ))}
        </select>
      </div>
      <div className="input-container">
        <label>Subject:</label>
        <select
          className="form-select"
          name="subject"
          value={subject}
          onChange={handleSubjectChange}
        >
          <option>Please select Subject</option>
          {Object.entries(deptObj)?.map(([departs, subjects], index) => {
            if (departs === "subjects") {
              return Object.entries(subjects)?.map(
                ([subCode, subDetails], index) => {
                  const sub = subDetails.sub;
                  const value = sub + subCode;
                  return (
                    <option key={index} value={value}>
                      {sub}
                    </option>
                  );
                }
              );
            }
          })}

          {/* {subjectData &&
            subjectData
              .find((depttObject) => depttObject.deptt === deptt)
              .subjects.map((subjectObject, index) => (
                <option key={index} value={subjectObject.subcode}>
                  {subjectObject.sub}
                  {console.log("subject code :-" + subjectObject.subcode)}
                </option>
              ))} */}
        </select>
      </div>
      {console.log(selectedSubject)}
      <div className="input-container">
        <label>Topic:</label>
        <select
          className="form-select"
          name="topic"
          value={topic}
          onChange={handleTopicChange}
        >
          <option>Please select Topic</option>
          {Object.entries(deptObj)?.map(([departs, subjects], index) => {
            if (departs === "subjects") {
              return Object.entries(subjects)?.map(([key, value], index) => {
                if (key === selectedSubject.subCode) {
                  return value.topics.map((topics, index) => {
                    const value =
                      "topicCode=" + topics.topcode + ", topic=" + topics.topic;
                    return (
                      <option key={index} value={value}>
                        {topics.topic}
                      </option>
                    );
                  });
                }
              });
            }
          })}
        </select>
      </div>
      <div className="input-container">
        <label>Difficulty:</label>
        <select
          value={difficulty}
          onChange={(e) => setDifficulty(e.target.value)}
          className="select-dropdown"
        >
          <option value="">Select difficulty</option>
          <option value="1">Easy</option>
          <option value="2">Medium</option>
          <option value="3">Hard</option>
        </select>
      </div>
      <div className="start-button-container">
        <button onClick={handleStartQuiz} className="start-button">
          Start Quiz
        </button>
      </div>
    </div>
  );
}

export default QuizSetup;
