import React, { useState, useEffect } from "react";
import axios from "axios";

const QbankPage = () => {
  const [formData, setFormData] = useState({
    subcode: "",
    topcode: "",
    validity: "",
    difficulty: "",
    question: "",
    option1: "",
    option2: "",
    option3: "",
    option4: "",
    answer: "",
    reference: "",
  });
  const [subjects, setSubjects] = useState([]);
  const [topcodes, setTopcodes] = useState([]);

  useEffect(() => {
    fetchSubjects();
  }, []);

  const fetchSubjects = async () => {
    try {
      const response = await axios.get(
        "https://railwaymcq.com/student/subMaster_api.php"
      );
      setSubjects(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchTopcodes = async () => {
    try {
      const response = await axios.get(
        `https://railwaymcq.com/student/topicMaster_api.php?subcode=${formData.subcode}`
      );
      setTopcodes(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    if (name === "subcode") {
      fetchTopcodes(); // Fetch topcodes when subcode changes
    }
  };

  const handleInsert = async () => {
    try {
      await axios.post("http://your-php-api-url/insert.php", formData);
      alert("Data inserted successfully!");
    } catch (error) {
      console.error(error);
      alert("Failed to insert data!");
    }
  };

  const handleUpdate = async () => {
    try {
      await axios.post("http://your-php-api-url/update.php", formData);
      alert("Data updated successfully!");
    } catch (error) {
      console.error(error);
      alert("Failed to update data!");
    }
  };

  const handleDelete = async () => {
    try {
      await axios.post(
        `http://your-php-api-url/delete.php?id=${formData.qcode}`
      );
      alert("Data deleted successfully!");
    } catch (error) {
      console.error(error);
      alert("Failed to delete data!");
    }
  };

  return (
    <div>
      <h2>Qbank Page</h2>
      <form>
        <select
          name="subcode"
          value={formData.subcode}
          onChange={handleInputChange}
        >
          <option value="">Select Subject</option>
          {subjects.map((subject) => (
            <option key={subject.subcode} value={subject.subcode}>
              {subject.sub}
            </option>
          ))}
        </select>
        <select
          name="topcode"
          value={formData.topcode}
          onChange={handleInputChange}
        >
          <option value="">Select Topic</option>
          {topcodes.map((topcode) => (
            <option key={topcode.topcode} value={topcode.topcode}>
              {topcode.topic}
            </option>
          ))}
        </select>
        {/* Rest of the form elements */}
        <button type="button" onClick={handleInsert}>
          Insert
        </button>
        <button type="button" onClick={handleUpdate}>
          Update
        </button>
        <button type="button" onClick={handleDelete}>
          Delete
        </button>
      </form>
    </div>
  );
};

export default QbankPage;
