import React, { useState, useEffect } from "react";
import axios from "axios";
import { useLocation } from "react-router-dom";

function Quiz() {
  const [quizData, setQuizData] = useState([]);
  const [userAnswers, setUserAnswers] = useState({});
  const [score, setScore] = useState(0);
  const location = useLocation();
  const { state: receivedData } = location;
  const searchParams = new URLSearchParams(location.search);

  const topcode = searchParams.get("topicCode");
  const difficulty = searchParams.get("difficulty");

  console.log("topic code..........." + receivedData?.topicCode);
  console.log("difficulty..........." + difficulty);

  useEffect(() => {
    // Fetch quiz data from the API

    const fetchQuizData = async () => {
      try {
        const response = await axios.get(
          "https://railwaymcq.com/student/mcq_api.php?topicCode=101&difficultyLevel=3"
          // `https://railwaymcq.com/student/mcq_api.php?topcode=${topcode}&difficulty=${difficulty}`
        );
        setQuizData(response.data);
      } catch (error) {
        console.error("Error fetching quiz data:", error);
      }
    };

    fetchQuizData();
  }, [searchParams]);

  const handleOptionSelect = (questionId, selectedOption) => {
    setUserAnswers({ ...userAnswers, [questionId]: selectedOption });
  };

  const handleSubmit = () => {
    // Calculate score based on user answers
    let totalScore = 0;
    quizData.forEach((question) => {
      if (userAnswers[question.qcode] === question.answer.trim()) {
        totalScore++;
      }
    });
    setScore(totalScore);
  };

  return (
    <div>
      <h1>Quiz</h1>
      {quizData.map((question) => (
        <div key={question.qcode}>
          <h3>{question.question}</h3>
          <ul>
            <li>
              <input
                type="radio"
                id={`${question.qcode}_option1`}
                name={question.qcode}
                value={question.option1}
                checked={userAnswers[question.qcode] === question.option1}
                onChange={() =>
                  handleOptionSelect(question.qcode, question.option1)
                }
              />
              <label htmlFor={`${question.qcode}_option1`}>
                {question.option1}
              </label>
            </li>
            <li>
              <input
                type="radio"
                id={`${question.qcode}_option2`}
                name={question.qcode}
                value={question.option2}
                checked={userAnswers[question.qcode] === question.option2}
                onChange={() =>
                  handleOptionSelect(question.qcode, question.option2)
                }
              />
              <label htmlFor={`${question.qcode}_option2`}>
                {question.option2}
              </label>
            </li>
            <li>
              <input
                type="radio"
                id={`${question.qcode}_option3`}
                name={question.qcode}
                value={question.option3}
                checked={userAnswers[question.qcode] === question.option3}
                onChange={() =>
                  handleOptionSelect(question.qcode, question.option3)
                }
              />
              <label htmlFor={`${question.qcode}_option3`}>
                {question.option3}
              </label>
            </li>
            <li>
              <input
                type="radio"
                id={`${question.qcode}_option4`}
                name={question.qcode}
                value={question.option4}
                checked={userAnswers[question.qcode] === question.option4}
                onChange={() =>
                  handleOptionSelect(question.qcode, question.option4)
                }
              />
              <label htmlFor={`${question.qcode}_option4`}>
                {question.option4}
              </label>
            </li>
          </ul>
        </div>
      ))}
      <button onClick={handleSubmit}>Submit</button>
      <p>Score: {score}</p>
    </div>
  );
}

export default Quiz;
