import React from "react";
import axios from "axios";
import { useState, useEffect } from "react";
function Plan() {
  state = {
    items: [],
    text: "",
  };
  const [getplan, setPlan] = useState([]);
  // const btnClicked = false;
  const [btnClicked, setBtnClicked] = useState(false);
  const [count, setCount] = useState(0);

  useEffect(() => {
    async function getAllPlan() {
      try {
        const plandata = await axios.get("http://127.0.0.1:8000/plan/list/");
        console.log("hi222222222222");
        console.log(plandata.data);
        setPlan(plandata.data);
      } catch (error) {
        console.log(error);
      }
    }
    getAllPlan();
  }, []);
  console.log("BTN CLICKED", btnClicked);

  // add plan for adding data

  useEffect(() => {
    if (btnClicked) {
      setCount(count + 1);

      const addPlan = (d) => {
        const addPlan = axios
          .post("http://127.0.0.1:8000/plan/create/", d)
          .then((res) => {
            this.setState({ text: "" });
            this.getAllPlan();
          });
      };
    }
    //  ? : null;
  }, [btnClicked]);
  // if (btnClicked) {
  //   setCount(count + 1);
  // }

  handleAdd = (event, e) => {
    let dt = { item: this.state.text };
    this.addPlan(dt);
  };

  handleChange = (event, e) => {
    this.setState({ text: e.target.value });
  };
  return (
    <div className="container-fluid my-5">
      <div className="row">
        <h1>Connect with Django</h1>;
        <div className="col-sm-6 mx-auto text-white shadow-lg p-3">
          <h2 className="text-center"> Today's Plan {count}</h2>
          <br />

          <div className="container-fluid">
            <div className="row">
              <div className="col-9">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Write Plan Here"
                  value={this.setState.text}
                  onChange={this.handleChange}
                />
              </div>
              <div className="col-2">
                <button
                  className="btn btn-warning px-3 font-weight-bold"
                  onClick={this.handleAdd}
                >
                  {/* {!btnClicked ? <>ADD</> : <>CANCLE</>} */}
                  Add
                </button>
              </div>
            </div>
            {btnClicked ? (
              <h3> yes we have plans for today</h3>
            ) : (
              <h3> we dont have any plans for today</h3>
            )}
            <div className="conatiner">
              <ul className="list-unstyled row m-5">
                {console.log("getplan", getplan)}
                {getplan?.map((items, i) => {
                  return (
                    <div>
                      <h2 key={i}>{items?.item}</h2>
                    </div>
                  );
                })}
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Plan;
