import React, { Component, useState, useEffect } from "react";
import axios from "axios";

// Axios Instance
const ai = axios.create({
  baseURL: "http://127.0.0.1:8000/",
});
//console.log(ai);
const Plan = () => {
  const [state, setState] = useState({
    items: [],
    text: "",
  });
  const [btn, setBtn] = useState(0);
  const showPlan = () => {
    ai.get("plan/list/").then((res) => {
      console.log(res.data);
      setState({ items: res.data });
    });
  };
  const addPlan = (d) => {
    console.log("Value of d is:-" + d.value);
    if (state.text !== "") {
      ai.post("plan/create/", d).then((res) => {
        setState({ text: "" });
        showPlan();
      });
    }
  };
  const handleChange = (e) => {
    setState({ text: e.target.value });
  };
  const handleAdd = (d) => {
    let dt = { item: state?.text };
    addPlan(dt);
    setBtn(btn + 1);
  };
  const handleDelete = (id) => {
    // console.log("Deleted", id);
    // alert(id);
    ai.delete(`plan/delete/${id}`).then((res) => {
      showPlan();
    });
  };
  useEffect(() => {
    showPlan();
  }, [btn]);

  return (
    <div className="container-fluid my-5">
      <div className="row">
        <div className="col-sm-6 mx-auto text-white shadow-lg p-3">
          <h2 className="text-center"> Today's Plan </h2>
          <div className="container-fluid">
            <div className="row">
              <div className="col-9">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Write Plan Here"
                  value={state.text}
                  onChange={handleChange}
                />
              </div>
              <div className="col-2">
                <button
                  className="btn btn-warning px-5 font-weight-bold"
                  onClick={handleAdd}
                >
                  Add
                </button>
              </div>
            </div>
            <div className="conatiner">
              <ul className="list-unstyled row m-5">
                {state?.items?.map((value, i) => {
                  console.log(value.id, value.item);
                  return (
                    <button
                      className="flex-growd-flex justify-content-left"
                      key={i}
                      id={value.id}
                      value={value.item}
                      sendData={handleDelete}
                    >
                      {value.id} - {value.item}
                      <button
                        class="btn btn-danger delete-btn"
                        onClick={() => handleDelete(value.id)}
                      >
                        Delete{" "}
                      </button>
                    </button>
                  );
                })}
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Plan;
