import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import "./feedback.css"; // Import CSS file
import { useInitialContext } from "../context/InitialContext";

// Axios Instance
const ai = axios.create({
  baseURL: "https://railwaymcq.com/student/dbfeedback_api.php",
});

export default function FeedBack_user(props) {
  const [formData, setFormData] = useState({
    zone: null,
    division: null,
    deptt: null,
    message: null,
    message_by: null,
  });
  const [errors, setErrors] = useState({});

  const {
    railwayInfo,
    zoneAndDivisionInfo,
    departmentInfo,
    SetZoneAndDivision,
  } = useInitialContext(); // Accessing zone and division data from context
  const { zoneAndDivisionData } = useInitialContext();
  const { departmentData } = useInitialContext();

  const handleChange = (e) => {
    const { name, value } = e.target;

    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));

    // Validate input
    if (name === "zone" && value === "") {
      //alert("hi");
      setErrors({ ...errors, zone: "Please select a zone" });
    } else if (name === "division" && value === "") {
      setErrors({ ...errors, division: "Please select a division" });
    } else if (name === "deptt" && value === "") {
      setErrors({ ...errors, deptt: "Please select a department" });
    } else {
      setErrors({ ...errors, [name]: "" });
    }
  };
  console.log(formData.zone !== "");
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (
      formData.zone &&
      formData.division &&
      formData.deptt &&
      formData.message &&
      formData.message_by
    ) {
      alert("Please fil data first");
    } else {
      try {
        // Check for validation errors before submitting
        if (Object.values(errors).some((error) => error !== "")) {
          alert("Please fix the errors before submitting the form");
          return false;
        }

        console.log("Form Data:", JSON.stringify(formData, null, 2));

        const response = await axios.post(
          "https://railwaymcq.com/student/dbfeedback_api.php",
          formData
        );
        console.log("Data added successfully:", response.data);
        alert("Data added successfully");

        setFormData({
          zone: "",
          division: "",
          deptt: "",
          message: "",
          message_by: "",
        });
      } catch (error) {
        console.error("Error adding data:", error);
      }
    }
  };

  useEffect(() => {
    SetZoneAndDivision();
  }, [SetZoneAndDivision]);

  return (
    <div className="feedback-container">
      <form onSubmit={handleSubmit}>
        <h6>{props.heading}</h6>
        <select
          className="form-select"
          name="zone"
          value={formData.zone}
          onChange={handleChange}
        >
          <option value="">Please select Zone</option>
          {zoneAndDivisionData &&
            zoneAndDivisionData.map((zones, index) =>
              Object.entries(zones).map(([zone], index) => {
                return (
                  <option key={index} value={zone}>
                    {zone}
                  </option>
                );
              })
            )}
        </select>
        {errors.zone && <p className="error">{errors.zone}</p>}

        <select
          className="form-select"
          name="division"
          value={formData.division}
          onChange={handleChange}
        >
          <option>Please select Division</option>
          {console.log(formData.zone)}
          {zoneAndDivisionData?.map((zones, zoneIndex) => {
            return Object.entries(zones)?.map(([zone, divisions]) => {
              if (zone === formData.zone) {
                return divisions.map((division, divisionIndex) => {
                  return (
                    <option key={divisionIndex} value={division}>
                      {division}
                    </option>
                  );
                });
              }
              return null; // Return value for map
            });
          })}
        </select>
        {errors.division && <p className="error">{errors.division}</p>}

        {/* Other form inputs */}

        <select
          className="form-select"
          name="deptt"
          value={formData.deptt}
          onChange={handleChange}
        >
          <option>Please select Department</option>
          {departmentData &&
            departmentData.map((depttObject, index) =>
              Object.entries(depttObject).map(([deptt], index) => (
                <option key={index} value={depttObject.dept_name}>
                  {depttObject.dept_name}
                </option>
              ))
            )}
        </select>
        {errors.deptt && <p className="error">{errors.deptt}</p>}

        <label htmlFor="mybox" className="form-label">
          <strong>Please feel free to leave any Feedback here</strong>
        </label>
        <textarea
          type="text"
          name="message"
          value={formData.message}
          onChange={handleChange}
          className="form-control"
          id="myBox"
          rows="4"
          col="8"
        ></textarea>

        <input
          type="text"
          className="custom-textbox" // Apply custom class for styling
          name="message_by"
          value={formData.message_by}
          onChange={handleChange}
          placeholder="Enter your good name please..."
          // placeholder={a.name}
        />

        {/*End of other inputs  */}

        <button type="submit" className="btn btn-primary">
          Save Feedback
        </button>
      </form>
    </div>
  );
}
